import axios from "axios";
import React from "react";
import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { types } from "../../../types/Types";

export const RegresionLineal = ({ history }) => {
    const search = useLocation().search;
    const searchPrestamo = new URLSearchParams(search).get("idprestamo");

    const [idPrestamo, setIdPrestamo] = useState("");
    const [Fecha_pago, setFecha_pago] = useState("");
    const [Referencia, setReferencia] = useState("");
    const [Monto_boleta, setMonto_boleta] = useState("");
    const [errBoleta, setErrBoleta] = useState("")
    const [errMonto, setErrMonto] = useState("")
    const [errFecha, setErrFecha] = useState("")

    const handleGuardar = (e) => {
        e.preventDefault();
        let blnHasErrorRef = false;
        let blnHasErrorMon = false;
        let blnHasErrorPag = false;

        if(Referencia==""){
            setErrBoleta("*Ingrese un boleta o referencia")
            blnHasErrorRef=true
         }else{
             setErrBoleta("")
             blnHasErrorRef=false
         }
         if(Monto_boleta==""){
            setErrMonto("*Ingrese el monto pagado")
            blnHasErrorMon=true
         }else{
             setErrMonto("")
             blnHasErrorMon=false
         }
        if(Fecha_pago==""){
           setErrFecha("*Ingrese la fecha de pago")
           blnHasErrorPag=true
        }else{
            setErrFecha("")
            blnHasErrorPag=false
        }

        if(blnHasErrorMon || blnHasErrorPag || blnHasErrorRef){
            return 
        }

        axios
            .post(types.apiurl + "/pagos/create", {
                idPrestamo: searchPrestamo,
                Fecha_pago,
                Referencia,
                Monto_boleta,
            })
            .then((respData) => {
                let valresp = respData.data;
                if (valresp.result == "OK") {
                    alert(valresp.msg);
                    history.push(`/pagos?idprestamo=${searchPrestamo}`);
                } else {
                    alert(valresp.msg);
                }
            });
    };

    return (
        <div className="pb-5 mb-5">
            <Link
                className="btn btn-lg btn-pink-sherbet fondo-red-crayola"
                to={`./pagos?idprestamo=${searchPrestamo}`}
            >
                Regresar
            </Link>
            <hr />
            <h1>Pago para prestamos {searchPrestamo}</h1>
            <form>
                <div className="form-group">
                    <label>Boleta:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="referencia"
                        value={Referencia}
                        //placeholder="Ingrese su referencia"
                        onChange={(event) => {
                            setReferencia(event.target.value);
                        }}
                    />
                    {
                        (errBoleta!=""?<p className="color-error">{errBoleta}</p>:<p></p>)
                    }
                </div>
                <div className="form-group">
                    <label>Monto:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="Monto_boleta"
                        value={Monto_boleta}
                        //placeholder="Ingrese su Monto_boleta"
                        onChange={(event) => {
                            setMonto_boleta(event.target.value);
                        }}
                    />
                    {
                        (errMonto!=""?<p className="color-error">{errMonto}</p>:<p></p>)
                    }
                </div>
                <div className="form-group">
                    <label>Fecha de pago:</label>
                    <input
                        type="date"
                        className="form-control"
                        id="Fecha_pago"
                        value={Fecha_pago}
                        //placeholder="Ingrese su Fecha_pago"
                        onChange={(event) => {
                            setFecha_pago(event.target.value);
                        }}
                    />{
                        (errFecha!=""?<p className="color-error">{errFecha}</p>:<p></p>)
                    }
                </div>
                <button
                    type="submit"
                    className="btn btn-lg btn-block fondo-pink-sherbet btn-lavender-blush"
                    onClick={handleGuardar}
                >
                    Agregar pago
                </button>
            </form>
        </div>
    );
};
