import axios from "axios";
import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import { types } from "../../../types/Types";
import { PrestamosItem } from "./PrestamosItem";

export const Prestamos = () => {
    const [listPrestamos, setListPrestamos] = useState([]);
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [DPI, setDPI] = useState("");

    useEffect(() => {
        axios
            .post(types.apiurl + "/prestamos/filtro", {
                Nombre: "",
                Apellido: "",
                DPI: "",
            })
            .then((resp) => {
                setListPrestamos(resp.data);
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);

    const handleSearch = (e) => {
        e.preventDefault();
        axios
        .post(types.apiurl + "/prestamos/filtro", {
            Nombre:nombre,
            Apellido:apellido,
            DPI,
        })
        .then((resp) => {
            setListPrestamos(resp.data);
        })
        .catch((err) => {
            console.log(err);
        });

    };

    return (
        <div>
            <div className=" pb-5 mb-5">
                <h1>Prestamos activos</h1>
                <hr />

                <div className="row">
                    <div className="col-5"></div>
                    <div className="col-5 pb-3">
                        <form onSubmit={handleSearch}>
                            <label>Nombres:</label>
                            <input
                                type="text"
                                placeholder="Nombres"
                                className="form-control"
                                name="nombres"
                                autoComplete="off"
                                value={nombre}
                                onChange={(event) => {
                                    setNombre(event.target.value);
                                }}
                            />

                            <label>Apellidos:</label>
                            <input
                                type="text"
                                placeholder="Apellidos"
                                className="form-control"
                                name="apellidos"
                                autoComplete="off"
                                value={apellido}
                                onChange={(event) => {
                                    setApellido(event.target.value);
                                }}
                            />

                            <div className="form-group">
                                <label>DPI:</label>
                                <input
                                    type="text"
                                    placeholder="DPI"
                                    className="form-control"
                                    name="DPI"
                                    autoComplete="off"
                                    value={DPI}
                                    onChange={(event) => {
                                        setDPI(event.target.value);
                                    }}
                                />
                            </div>
                            <button
                                type="submit"
                                className="btn btn-lg btn-block fondo-pink-sherbet btn-lavender-blush"
                            >
                                Buscar
                            </button>
                        </form>
                    </div>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th>DPI</th>
                            <th>Nombre</th>
                            <th>Apellido</th>
                            <th>Modalidad</th>
                            <th>Interes</th>
                            <th>Monto concedido</th>
                            <th>Monto Neto</th>
                            <th>Monto pagado</th>
                            <th>Saldo</th>
                            <th>Num. cuotas pendientes</th>
                            {/*<th>Proxima fecha de pago</th>*/}
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {listPrestamos.map((prestamo) => (
                            <PrestamosItem
                                key={prestamo.idLector}
                                {...prestamo}
                            />
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};
