import axios from "axios";
import React from "react";
import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { types } from "../../../types/Types";

export const RegresionLineal = ({ history }) => {

    const [dependiente, setDependiente] = useState("");
    const [independiente, setIndependiente] = useState("");
    const [path_csv, setpath_csv] = useState("");

    const handleCalcular = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("name", "name");
        formData.append("archivo", path_csv);

        axios.post(types.apiurl+"uploadfile", formData)
        .then(data =>{
            console.log("uplodadfile")
        })

        axios
            .post(types.apiurl + "regresionlineal", {
                dependiente,
                independiente,

            })
            .then((respData) => {
                let valresp = respData.data;
                if (valresp.result == "OK") {
                    alert(valresp.msg);
                } else {
                    alert(valresp.msg);
                }
            });
    };

    return (
        <div className="pb-5 mb-5">
            <h1>Regresion Lineal</h1>
            <hr />

            <form>
                <div className="form-group">
                    <label>Dependiente Y:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="referencia"
                        value={dependiente}
                        //placeholder="Ingrese su referencia"
                        onChange={(event) => {
                            setDependiente(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Independiente X:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="referencia"
                        value={independiente}
                        //placeholder="Ingrese su referencia"
                        onChange={(event) => {
                            setIndependiente(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Archivo:</label>
                    <div className="form-group">
                        <input
                            className="form-control-file"
                            id="fileSelector"
                            type="file"
                            onChange={(e) => setpath_csv(e.target.files[0])}
                        />
                    </div>
                </div>
                <button
                    type="submit"
                    className="btn btn-lg btn-block btn-pink-sherbet fondo-red-crayola"
                    onClick={handleCalcular}
                >
                    Calcular
                </button>
            </form>
        </div>
    );
};
