import React from "react";
import { useHistory } from 'react-router-dom'

export const ClienteItem = ({idCliente,DPI, Nombre, Apellido, Telefono, Email, Fecha_nac, Direccion}) => {

    let history = useHistory();
    let fecha_n = Fecha_nac
    let fecha_y = fecha_n.split("T")

    const handleEditar = () => {
        history.push(`/clientesabc?edit=1&idCliente=${idCliente}`)
    };

    const gotoprestamo = () => {
        history.push(`/prestamoabc?idCliente=${idCliente}`)
    };

    const gotocredito = () => {
        history.push(`/creditoabc?idCliente=${idCliente}`)
    };

    return (


        <tr>
            <td>{DPI}</td>
            <td>{Nombre}</td>
            <td>{Apellido}</td>
            <td>
                {Telefono.length > 0 ? (
                    <a
                        className="btn btn-outline-success"
                        href={"https://wa.me/502" + Telefono}
                    >
                        {Telefono}
                    </a>
                ) : (
                    <></>
                )}
            </td>
            <td>{Email}</td>
            <td>{Direccion}</td>
            <td>{fecha_y[0]}</td>
            <td>
                <button 
                    className="btn btn-out-light-coral"
                    onClick={handleEditar}
                >
                    Editar Datos
                </button>
                </td>
            <td>
                <button 
                    className="btn btn-out-pink-sherbet"
                    onClick={gotoprestamo}
                >
                    Crear Prestamo
                </button>


            </td>
            <td>
                <button 
                    className="btn btn-out-red-crayola"
                    onClick={gotocredito}
                >
                    Compra Cr√©dito
                </button>


            </td>
        </tr>
    );
};
