import axios from "axios";
import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { types } from "../../../types/Types";

export const ClienteABC = ({ history }) => {
    const search = useLocation().search;
    const isedit = new URLSearchParams(search).get("edit");

    const [titulo, setTitulo] = useState("Cliente nuevo");

    const [idCliente, setIdCliente] = useState("");
    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [DPI, setDPI] = useState("");
    const [telefono, setTelefono] = useState("");
    const [direccion, setDireccion] = useState("");
    const [email, setEmail] = useState("");
    const [fechaNac, setFechaNac] = useState("");
    const [msgButton, setMsgButton] = useState("Crear Cliente")

    useEffect(() => {
        if (isedit == "1") {
            setTitulo("Editar Cliente");
            setMsgButton("Actualizar Cliente")
            const searchCliente = new URLSearchParams(search).get("idCliente");
            setIdCliente(searchCliente);
            axios
                .post(types.apiurl + "/clientes/byid", {
                    idCliente: searchCliente,
                })
                .then((respData) => {
                    let fecha_n = respData.data[0].Fecha_nac;
                    let fecha_y = fecha_n.split("T");

                    setDPI(respData.data[0].DPI);
                    setNombre(respData.data[0].Nombre);
                    setApellido(respData.data[0].Apellido);
                    setTelefono(respData.data[0].Telefono);
                    setDireccion(respData.data[0].Direccion);
                    setEmail(respData.data[0].Email);
                    setFechaNac(fecha_y[0]);
                });
        }
    }, []);

    const handleGuardar = async (e) => {
        e.preventDefault();
        if (isedit == 1) {
            await axios
                .put(types.apiurl + "/clientes", {
                    idCliente,
                    DPI,
                    nombre,
                    apellido,
                    telefono,
                    direccion,
                    email,
                    fechaNac,
                })
                .then((respData) => {
                    let valresp = respData.data;
                    if (valresp.result == "OK") {
                        alert(
                            valresp.msg + " " + valresp.nombreCliente
                        );

                        history.push("/clientes");
                    } else {
                        alert(valresp.msg);
                    }
                });
        } else {
            //Creacion
            await axios
                .post(types.apiurl + "/clientes", {
                    DPI,
                    nombre,
                    apellido,
                    telefono,
                    direccion,
                    email,
                    fechaNac,
                })
                .then((respData) => {
                    let valresp = respData.data;
                    if (valresp.result == "OK") {
                        alert(
                            valresp.msg +
                                ". (" +
                                valresp.idCliente +
                                ") " +
                                valresp.nombreCliente
                        );

                        history.push("/clientes");
                    } else {
                        alert(valresp.msg);
                    }
                });
        }
    };

    return (
        <div className="pb-5 mb-5">
            <Link
                className="btn btn-lg btn-pink-sherbet fondo-red-crayola"
                to={`./clientes`}
            >
                Regresar
            </Link>
            <hr />
            <h1>{titulo}</h1>
            <form>
                <div className="form-group">
                    <label>DPI:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="DPI"
                        value={DPI}
                        //placeholder="Ingrese su DPI"
                        onChange={(event) => {
                            setDPI(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Nombre:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="nombre"
                        value={nombre}
                        //placeholder="Ingrese su nombre"
                        onChange={(event) => {
                            setNombre(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Apellido:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="apellido"
                        value={apellido}
                        //placeholder="Ingrese su Apellido"
                        onChange={(event) => {
                            setApellido(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Telefono:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="telefono"
                        value={telefono}
                        //placeholder="Ingrese su Telefono"
                        onChange={(event) => {
                            setTelefono(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Direccion:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="Direccion"
                        value={direccion}
                        //placeholder="Ingrese su Direccion"
                        onChange={(event) => {
                            setDireccion(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Email:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="Email"
                        value={email}
                        //placeholder="Ingrese su Email"
                        onChange={(event) => {
                            setEmail(event.target.value);
                        }}
                    />
                </div>
                <div className="form-group">
                    <label>Fecha Nacimiento:</label>
                    <input
                        type="date"
                        className="form-control"
                        id="fechaNac"
                        value={fechaNac}
                        //placeholder=""
                        onChange={(event) => {
                            setFechaNac(event.target.value);
                        }}
                    />
                </div>
                <button
                    type="submit"
                    className="btn btn-lg btn-block fondo-pink-sherbet btn-lavender-blush"
                    onClick={handleGuardar}
                >
                    {msgButton}
                </button>
            </form>
        </div>
    );
};
