import React from 'react'
import { useHistory } from 'react-router-dom'

export const PrestamosItem = ({idPrestamo, DPI, Nombre, Apellido, Modalidad, Interes, Cantidad_otorgada,Cantidad_neta, Monto_cancelado_c, Cuotas_pagadas_c, Saldo, Num_cuotas, Fecha_fin}) => {

    let history = useHistory();
    let fecha_n = Fecha_fin
    let fecha_y = fecha_n.split("T")

    const gotopagos = (e) => {
        history.push(`/pagos?idprestamo=${idPrestamo}`)
    };

  return (
    <tr>
            <td>{DPI}</td>
            <td>{Nombre}</td>
            <td>{Apellido}</td>
            <td>{Modalidad}</td>
            <td>{Interes}</td>
            <td>{Cantidad_otorgada}</td>
            <td>{Cantidad_neta}</td>
            <td>{Monto_cancelado_c}</td>
            <td>{Cantidad_neta - Monto_cancelado_c}</td>
            <td>{Cantidad_neta - Monto_cancelado_c <= 0 ? 0 : Num_cuotas-Cuotas_pagadas_c}</td>
            {/*<td>{fecha_y[0]}</td>*/}
            <td>
                <button
                    className="btn btn-out-red-crayola"
                    onClick={gotopagos}
                >
                    Ver pagos
                </button>
            </td>
        </tr>
  )
}
