import axios from "axios";
import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { types } from "../../../types/Types";
import { PagoItem } from "./PagoItem";

export const Pagos = () => {
    const search = useLocation().search;
    const searchPrestamo = new URLSearchParams(search).get("idprestamo");

    const [listPagos, setListPagos] = useState([]);
    const [sumPago, setSumPago] = useState("");
    const [idPrestamo, setIdPrestamo] = useState("");
    const [Fecha_pago, setFecha_pago] = useState("");
    const [Referencia, setReferencia] = useState("");
    const [Monto_boleta, setMonto_boleta] = useState("");

    useEffect(() => {
        axios
            .post(types.apiurl + "/pagos/getalldata", {
                idPrestamo: searchPrestamo,
            })
            .then((resp) => {
                setListPagos(resp.data);
            });
    }, []);

    useEffect(() => {
        axios
            .post(types.apiurl + "/pagos/postsum", {
                idPrestamo: searchPrestamo,
            })
            .then((resp) => {
                setSumPago(resp.data[0].total);
            });
    }, []);

    return (
        <div className=" pb-5 mb-5">
            <h1>Pagos realizados para {searchPrestamo}</h1>
            <hr />
            <table className="table">
                <thead>
                    <tr>
                        <th>idPago</th>
                        <th>Fecha de pago</th>
                        <th>Referencia o boleta</th>
                        <th>Monto</th>
                    </tr>
                </thead>
                <tbody>
                    {listPagos.map((prestamo) => (
                        <PagoItem key={prestamo.idPago} {...prestamo} />
                    ))}
                    {
                        <tr>
                            <td colspan="3"><b>Total Pagado:</b></td><td><b>{sumPago?sumPago:0}</b></td>
                        </tr>
                    }
                </tbody>
            </table>
            <div className="row">
                <div className="col-10"></div>
                <div className="col-2">
                    <br />
                    <Link
                        className="btn btn-lg btn-pink-sherbet fondo-red-crayola"
                        to={`./pagosabc?idprestamo=${searchPrestamo}`}
                    >
                        Agregar pago
                    </Link>
                    <br /> <br />
                </div>
            </div>
        </div>
    );
};
